/**
 * Mock for BandActivity type generated by Hilla.
 * Used in frontend tests to avoid Gradle dependency.
 */
interface BandActivity {
  band?: string;
  mode?: string;
  spotCount?: number;
  baselineSpotCount?: number;
  trendPercentage?: number;
  maxDxKm?: number;
  maxDxPath?: string;
  activePaths?: string[];
  score?: number;
  windowMinutes?: number;
}

export default BandActivity;
